		@JoinTable(type=Type.LEFT_JOIN, entity=Office.class, attrName="${c.simpleAttrName}", alias="u${cLP.index}",
			on="u${cLP.index}.office_code = a.${c.columnName}", columns={
				@Column(name="office_code", label="机构编码", isPK=true),
