		if (isNotBlank(joinTables)){
			print('joinTable={\n');
			print(joinTables);
			print('\t}, ');
		}
		// ◆ 生成排序字段
	%>orderBy="<% if (isTree){
						%>a.tree_sorts<%
							for(pk in table.pkList){
								%>, a.${pk.columnName}<%
							}
					}else if(table.parentExists && table.createDateExists){
						%>a.create_date ASC<%
					}else if(table.updateDateExists){
						%>a.update_date DESC<%
					}else{
						for(pk in table.pkList){
							%>${pkLP.index!=1?', ':''}a.${pk.columnName} ${table.parentExists?'ASC':'DESC'}<%
						}
					} %>"
