mapper.select:
SELECT 

FROM test_data_main a

WHERE a.id = #{id}

SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test_data_main SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
