mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT
FROM cms_comment a
a.del_flag = #{delFlag}
AND a.title LIKE '%'||#{title}||'%' CONCAT('%', #{title}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cms_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
