mapper.select:
SELECT 

FROM cms_site a

WHERE a.id = #{id}

SELECT
FROM cms_site a
a.del_flag = #{delFlag}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cms_site a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_site SET 
	del_flag = #{delFlag}
WHERE id = #{id}
