mapper.select:
SELECT 

FROM OA_NOTIFY a

WHERE a.id = #{id}

SELECT
, r.read_flag
FROM OA_NOTIFY a
JOIN OA_NOTIFY_RECORD r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.TYPE = #{type}
AND a.STATUS = #{status}
AND a.STATUS = '1'
ORDER BY a.update_date DESC

SELECT 
	count(1)
FROM OA_NOTIFY a

	JOIN OA_NOTIFY_RECORD r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}

			AND r.read_flag = #{readFlag}


WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.STATUS = '1'

mapper.update:
UPDATE OA_NOTIFY SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
